const make_chances = require("make_chances")
let userObject = {
    id: "integer",
    name: "name",
    email: "email",
    hash: "hash",
    fb_token: "social_app_tokens",
    created: "date",
    verified: "boolean",
    phone: "phone",
    address: "address"
}
make_chances.create(userObject, 20)
    // returns an array

make_chances.createDirty(userObject, 20)
    // integers will be in strings
    // strings will be undefined or too long
    // emails will be not be valid
    // empty hash
    // date will be in weird formats
    // booleans will be in strings